var valorASerConvertido = prompt("Quanto você gatou em reais?")

valorASerConvertido = parseFloat(valorASerConvertido)
valorASerConvertido = valorASerConvertido/5.51
valorASerConvertido = valorASerConvertido.toFixed(2)

alert(valorASerConvertido)

